export const initialState = {
    mainPosts: [{
        id: 1,
        Usr: {
            id: 1,
            nickname: 'LEA',
        },
        content: '첫 번째 게시글 #해시태그 #익스프레스',
        Images: [{
            src: '',
        }, {
            src: '',
        }, {
            src: '',
        }],
        Comments: [{
            User: {
                nickname: 'nero',
            },
            content: '우와 안녕하세요 첫번째 코멘트입니다.',
        },
        {
            User: {
                nickname: 'como',
            },
            content: '와아 집에 가고싶다 ㅜㅜ',
        }]
    }],
    imagePaths: [],
    postAdded: false,
}

const ADD_POST = 'ADD_POST';
export const addPost = {
    type: ADD_POST,
}
const dummyPost = {
    id: 2,
    content: '더미데이터입니다',
    User: {
        id: 1,
        nickname: 'LEA',
    },
    Images: [],
    Comments: [],
}

const reducer = ( state = initialState, action ) => {
    switch (action.type) {
        case ADD_POST:
            return {
                ...state,
                mainPosts: [dummyPost, ...state.mainPost],
                postAdded: true,
            }
        default:
            return state;
    }
};

export default reducer;
