export const initialState = {
    isLoggingIn: false, // 로그인 시도 중
    isLoggedIn: false,
    isLoggingOut: false, // 로그아웃 시도 중
    me: null,
    signUpDate: {},
    loginData: {},
}

export const loginAction = (data) => {
    return (dispatch, getState) => {
        const state = getState();
        dispatch(loginRequestAction());
        axios.post('/api/login')
            .then((res) => {
                dispatch(loginSuccessAction(res.data));
            })
            .catch ((err)=> {
                dispatch(loginFailureAction(err));
            })
    }
}


// action creator
export const loginRequestAction = (data) => {
    return {
        type: 'LOG_IN_REQUEST',
        data,
    }
}

export const logoutRequestAction = () => {
    return {
        type: 'LOG_OUT_REQUEST',
    }
}

const reducer = ( state = initialState, action ) => {
    switch (action.type) {
        case 'LOG_IN_REQUEST':
            console.log("reducer login");
            return {
                ...state,
                isLoggingIn: true,
                me: action.data,
            };
        case 'LOG_IN_SUCCESS':
            return {
                ...state,
                isLoggingIn: false,
                isLoggedIn: true,
                me: { ...action.data, nickname: 'learomero'}

            };
        case 'LOG_IN_FAILURE':
            return {
                ...state,
                isLoggingIn: false,
                isLoggedIn: true,
                me: action.data,
            };
        case 'LOG_OUT_REQUEST':
            return {
                ...state,
                isLoggingOut: true,
                isLoggedIn: false,
                me: null,
            };
        case 'LOG_OUT_SUCCESS':
            return {
                ...state,
                isLoggingOut: false,
                isLoggedIn: true,
                me: action.data,
            };
        case 'LOG_OUT_FAILURE':
            return {
                ...state,
                isLoggingOut: false,
                isLoggedIn: true,
                me: action.data,
            };
        default:
            return state;
    }
};

export default reducer;
